}

function Header({ onLogin, actualUser, onLogOut }) {
  return (
    <header className="header">
      <div className="container-header">
        <img className="icon" src="./icon.png" alt="delta icon" />
        <div className="titles">
          <h1>Transtornos del sueño</h1>
          <h2>Bienvenido</h2>
        </div>
        {!actualUser ? (
          <Login onLogin={onLogin} />
        ) : (
          <div className="login">
            <label>Hola, {actualUser.name}!</label>
            <br />
            <Button onClick={onLogOut}>Cerrar sesion</Button>
          </div>
        )}
      </div>
    </header>
  );
}

function Login({ onLogin }) {
  const users = initialUsers;
  const [inputUser, setInputUser] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (!inputUser) return;

    users.map((user) =>
      user.id === inputUser ? onLogin(user) : alert("Usuario no encontrado")
    );
  }

  return (
    <form className="login" onSubmit={handleSubmit}>
      <p>Ingresar</p>
      <input
        type="text"
        placeholder="Matricula institucional"
        value={inputUser}
        onChange={(e) => setInputUser(e.target.value)}
      />
      <input type="submit" />
    </form>
  );
}

function Form() {
  const questions = initialQuestions;
  const [actualQuestion, setActualQuestion] = useState(1);
  const [answers, setAnswer] = useState([]);

  function handleSetAnswer(e) {
    const answer1 = {
      name: e.target.name,
      value: e.target.value,
    };

    setAnswer((prevAnswers) => {
      const exists = prevAnswers.some((answer) => answer.name === answer1.name);

      if (exists) {
        return prevAnswers.map((answer) =>
          answer.name === answer1.name
            ? { ...answer, value: answer1.value }
            : answer
        );
      } else {
        return [...prevAnswers, answer1];
      }
    });
  }

  function handleNextQuestion() {
    setActualQuestion((cur) => (cur < questions.length ? cur + 1 : cur));
  }

  function handlePreviousQuestion() {
    setActualQuestion((cur) => (cur > 1 ? cur - 1 : cur));
  }

  function handleSubmit() {
    console.log("hola");
  }

  return (
    <>
      <form className="entire-form" onSubmit={handleSubmit}>
        <ul>
          {questions.map((question) => (
            <Question
              question={question}
              key={question.question}
              actualQuestion={actualQuestion}
              onSetAnswer={handleSetAnswer}
            />
          ))}
        </ul>
      </form>
      <div className="buttons">
        <Button onClick={handlePreviousQuestion} type="button">
          Anterior
        </Button>
        <Button onClick={handleNextQuestion} type="button">
          Siguiente
        </Button>
      </div>
    </>
  );
}

function Button({ children, onClick, type }) {
  return (
    <button type={type} onClick={onClick}>
      {children}
    </button>
  );
}

function Question({ question, actualQuestion, onSetAnswer }) {
  return (
    <div className="question">
      {actualQuestion === question.num ? (
        <li>
          <p>{question.question}</p>
          <>
            {question.options.length > 0 ? (
              question.options.map((option, i) => (
                <Option
                  num={question.num}
                  option={option}
                  key={option}
                  i={i}
                  onSetAnswer={onSetAnswer}
                />
              ))
            ) : (
              <textarea name={question.num} rows={4} cols={50} />
            )}
          </>
        </li>
      ) : null}
    </div>
  );
}

function Option({ num, option, i, onSetAnswer }) {
  return (
    <>
      <input type="radio" name={num} value={i} onChange={onSetAnswer} />
      <label>{option}</label>
      <br />
    </>
  );
}

