export default function App() {
  const [actualUser, SetActualUser] = useState(null);

  function handleLogin(id) {
    SetActualUser(id);
  }

  function onLogOut() {
    SetActualUser("");
  }

  return (
    <div className="app">
      <Header
        onLogin={handleLogin}
        actualUser={actualUser}
        onLogOut={onLogOut}
      />
      <Form />
      <AboutUs />
      <Footer />
    </div>
  );
}

function AboutUs() {
  return (
    <div className="about-us">
      <Button>Acerca de nosotros</Button>
      <Button>Servicios</Button>
      <Button>Testimonios</Button>
    </div>
  );
}

function Header({ onLogin, actualUser, onLogOut }) {
  return (
    <header className="header">
      <img className="icon" src="./icon.png" alt="delta icon" />
      <div className="titles">
        <h1>Transtornos del sueño</h1>
        <h2>Bienvenido</h2>
      </div>
      {!actualUser ? (
        <Login onLogin={onLogin} />
      ) : (
        <div className="login">
          <label>Hola, {actualUser.name}!</label>
          <br />
          <Button onClick={onLogOut}>Cerrar sesion</Button>
        </div>
      )}
    </header>
  );
}

function Login({ onLogin }) {
  const users = initialUsers;
  const [inputUser, setInputUser] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (!inputUser) return;

    users.map((user) => user.id === inputUser && onLogin(user));
  }

  return (
    <form className="login" onSubmit={handleSubmit}>
      <p>Ingresar</p>
      <input
        type="text"
        placeholder="Matricula institucional"
        value={inputUser}
        onChange={(e) => setInputUser(e.target.value)}
      />
      <input type="submit" />
    </form>
  );
}

function Form() {
  const questions = initialQuestions;
  const [actualQuestion, setActualQuestion] = useState(1);
  const [answers, setAnswers] = useState([]);

  function handleSetAnswer(e) {
    const { name, value } = e.target;
    setAnswers((prevAnswers) =>
      prevAnswers.some((answer) => answer.name === name)
        ? prevAnswers.map((answer) =>
            answer.name === name ? { ...answer, value } : answer
          )
        : [...prevAnswers, { name, value }]
    );
  }

  function handleNextQuestion() {
    setActualQuestion((cur) => (cur < questions.length ? cur + 1 : cur));
  }

  function handlePreviousQuestion() {
    setActualQuestion((cur) => (cur > 1 ? cur - 1 : cur));
  }

  function handleSubmit() {
    console.log("hola");
  }

  return (
    <>
      <form className="entire-form" onSubmit={handleSubmit}>
        {questions.map((question) => (
          <Question
            question={question}
            key={question.question}
            actualQuestion={actualQuestion}
            onSetAnswer={handleSetAnswer}
            answers={answers}
          />
        ))}
        <div className="select-buttons">
          <Button onClick={handlePreviousQuestion} type="button">
            Anterior
          </Button>
          <Button onClick={handleNextQuestion} type="button">
            Siguiente
          </Button>
        </div>
      </form>
    </>
  );
}

function Button({ children, onClick, type }) {
  return (
    <button type={type} onClick={onClick}>
      {children}
    </button>
  );
}

function Question({ question, actualQuestion, onSetAnswer, answers }) {
  return (
    <>
      {actualQuestion === question.num ? (
        <>
          <p className="question-text">{question.question}</p>
          <div className="options">
            <ul>
              <li>
                {question.options.length > 0 ? (
                  question.options.map((option, i) => (
                    <Option
                      num={question.num}
                      option={option}
                      key={option}
                      i={i}
                      onSetAnswer={onSetAnswer}
                      answers={answers}
                    />
                  ))
                ) : (
                  <textarea name={question.num} rows={4} cols={50} />
                )}
              </li>
            </ul>
          </div>
        </>
      ) : null}
    </>
  );
}

function Option({ num, option, i, onSetAnswer, answers }) {
  return (
    <div className="option">
      <div>
        <input
          type="radio"
          name={num}
          value={i}
          onChange={onSetAnswer}
          id={i}
        />
        <span>{i === 0 && A}</span>
      </div>
      <label htmlFor={i}>{option}</label>
    </div>
  );
}

function Footer() {
  return (
    <footer>
      <div className="slogan">
        <p>
          Tu fuente confiable para mejorar el bienestar y el sueño saludable.
        </p>
      </div>
      <div className="contact">
        <p>
          Contáctanos: <br />
          Correo electrónico: contacto@deltainc.com <br />
          Teléfono: +57 123 456 7890
        </p>
      </div>
      <div className="copyright">
        <span>© 2024 Delta Inc. Todos los derechos reservados.</span>
      </div>
    </footer>
  );
}
