import { useState } from "react";
import "./App.css";

const initialQuestions = [
  // Insomnio
  {
    num: 1,
    disorder: "Insomnio",
    question: "¿Tienes dificultad para quedarte dormido(a)?",
  },
  {
    id: 2,
    disorder: "Insomnio",
    question:
      "¿Te despiertas varias veces durante la noche y tienes dificultad para volver a dormir?",
  },
  {
    id: 3,
    disorder: "Insomnio",
    question:
      "¿Te despiertas antes de la hora deseada y no puedes volver a dormir?",
  },
  {
    id: 4,
    disorder: "Insomnio",
    question:
      "¿Sientes que tu sueño no es reparador, como si no hubieras descansado?",
  },
  {
    id: 5,
    disorder: "Insomnio",
    question:
      "¿Sientes somnolencia, cansancio o mal humor durante el día por la falta de sueño?",
  },

  // Trastorno del Ritmo Circadiano
  {
    id: 6,
    disorder: "Trastorno del Ritmo Circadiano",
    question:
      "¿Sueles quedarte despierto(a) hasta muy tarde y te cuesta mucho despertarte en la mañana?",
  },
  {
    id: 7,
    disorder: "Trastorno del Ritmo Circadiano",
    question: "¿Te resulta difícil seguir un horario de sueño regular?",
  },
  {
    id: 8,
    disorder: "Trastorno del Ritmo Circadiano",
    question:
      "¿Sientes que tu cuerpo 'funciona' mejor en horarios no convencionales, como por la noche o madrugada?",
  },
  {
    id: 9,
    disorder: "Trastorno del Ritmo Circadiano",
    question:
      "¿Experimentas dificultades para adaptarte a horarios de sueño tempranos?",
  },
  {
    id: 10,
    disorder: "Trastorno del Ritmo Circadiano",
    question:
      "¿Te cuesta rendir en actividades que requieren estar despierto(a) temprano?",
  },

  // Apnea del Sueño
  {
    id: 11,
    disorder: "Apnea del Sueño",
    question:
      "¿Te han dicho que roncas de forma fuerte o constante mientras duermes?",
  },
  {
    id: 12,
    disorder: "Apnea del Sueño",
    question:
      "¿Has experimentado pausas en la respiración mientras duermes (te lo han contado o has notado que te despiertas sin aliento)?",
  },
  {
    id: 13,
    disorder: "Apnea del Sueño",
    question: "¿Te despiertas con sensación de asfixia o de que falta aire?",
  },
  {
    id: 14,
    disorder: "Apnea del Sueño",
    question:
      "¿Sientes sueño excesivo durante el día, incluso después de una noche completa de sueño?",
  },
  {
    id: 15,
    disorder: "Apnea del Sueño",
    question: "¿Tienes dolores de cabeza frecuentes al despertar?",
  },

  // Parasomnias
  {
    id: 16,
    disorder: "Parasomnias",
    question:
      "¿Te han contado que hablas, te mueves o caminas dormido(a) (sonambulismo)?",
  },
  {
    id: 17,
    disorder: "Parasomnias",
    question:
      "¿Experimentas pesadillas frecuentes que te despiertan con angustia?",
  },
  {
    id: 18,
    disorder: "Parasomnias",
    question:
      "¿Has experimentado 'terrores nocturnos' en los que te despiertas asustado(a) y sin saber bien por qué?",
  },
  {
    id: 19,
    disorder: "Parasomnias",
    question:
      "¿Te despiertas con movimientos bruscos o sientes que tus sueños afectan tu comportamiento durante la noche?",
  },
  {
    id: 20,
    disorder: "Parasomnias",
    question:
      "¿Sientes temor a dormir debido a sueños o eventos inusuales que te ocurren mientras duermes?",
  },

  // Narcolepsia
  {
    id: 21,
    disorder: "Narcolepsia",
    question:
      "¿Experimentas episodios de somnolencia extrema durante el día, incluso en momentos poco comunes?",
  },
  {
    id: 22,
    disorder: "Narcolepsia",
    question:
      "¿Te quedas dormido(a) repentinamente en situaciones que requieren estar despierto(a) (como en clase, reuniones, etc.)?",
  },
  {
    id: 23,
    disorder: "Narcolepsia",
    question:
      "¿Tienes episodios de pérdida repentina de tono muscular, especialmente en momentos de risa o emoción intensa?",
  },
  {
    id: 24,
    disorder: "Narcolepsia",
    question:
      "¿Has experimentado 'parálisis del sueño' (no puedes moverte al despertar o al quedarte dormido)?",
  },
  {
    id: 25,
    disorder: "Narcolepsia",
    question:
      "¿Sientes que tienes sueños muy vívidos y realistas justo al quedarte dormido o al despertar?",
  },
];

export default function App() {
  return (
    <div className="app">
      <Header />
      <Form />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <div className="container-header">
        <img className="icon" src="./icon.png" alt="delta icon" />
        <div className="titles">
          <h1>Transtornos del sueño</h1>
          <h2>Bienvenido</h2>
        </div>
        <Login />
      </div>
    </header>
  );
}

function Login() {
  return (
    <div className="login">
      <p>Ingresar</p>
      <input type="text" placeholder="Matricula institucional" />
    </div>
  );
}

function Form() {
  const questions = initialQuestions;
  const [actualQuestion, setActualQuestion] = useState(0);

  function handleNextQuestion() {
    if (actualQuestion < questions.length - 1)
      setActualQuestion((cur) => cur + 1);
  }

  function handlePreviousQuestion() {
    if (actualQuestion > 0) setActualQuestion((cur) => cur - 1);
  }

  function handleSubmit() {
    console.log("hola");
  }

  return (
    <>
      <form className="entire-form" onSubmit={handleSubmit}>
        <ul>
          {questions.map((question) => (
            <Question
              question={question}
              key={question.question}
              actualQuestion={actualQuestion}
            />
          ))}
        </ul>
      </form>
      <div className="buttons">
        <Button onClick={handlePreviousQuestion} type="button">
          Anterior
        </Button>
        <Button onClick={handleNextQuestion} type="button">
          Siguiente
        </Button>
      </div>
    </>
  );
}

function Button({ children, onClick, type }) {
  return (
    <button type={type} onClick={onClick}>
      {children}
    </button>
  );
}

function Question({ question, actualQuestion }) {
  return (
    <div className="question">
      {actualQuestion === question.num ? (
        <li>
          <p>{question.question}</p>
          <div>
            {question.options.length > 0 ? (
              question.options.map((option) => (
                <Option num={question.num} option={option} key={option} />
              ))
            ) : (
              <textarea name={question.num} rows={4} cols={50} />
            )}
          </div>
        </li>
      ) : null}
    </div>
  );
}

function Option({ num, option }) {
  return (
    <div>
      <input type="radio" name={num} />
      <label>{option}</label>
    </div>
  );
}

function Footer() {
  return (
    <footer>
      <div>
        <p>
          Tu fuente confiable para mejorar el bienestar y el sueño saludable.
        </p>
      </div>
      <div>
        <ul>
          <li>Acerca de nosotros</li>
          <li>Servicios</li>
          <li>Testimonios</li>
        </ul>
      </div>
      <div>
        <p>
          Contáctanos: <br />
          Correo electrónico: contacto@deltainc.com <br />
          Teléfono: +57 123 456 7890
        </p>
      </div>
      <div>
        <span>© 2024 Delta Inc. Todos los derechos reservados.</span>
      </div>
    </footer>
  );
}
