import "./App.css";

const initialQuestions = [
  {
    question: "¿Cuál es tu edad?",
    options: [
      "18-25 años",
      "26-35 años",
      "36-45 años",
      "46-55 años",
      "56 años o más",
    ],
  },
  {
    question: "¿Cuál es tu género?",
    options: ["Masculino", "Femenino", "Otro / Prefiero no decirlo"],
  },
  {
    question: "¿En qué país resides?",
    options: [],
  },
  {
    question:
      "¿Has experimentado alguna dificultad para dormir en los últimos 6 meses?",
    options: ["Sí, frecuentemente", "A veces", "Raramente", "No"],
  },
  {
    question:
      "¿Alguna vez has sido diagnosticado con un trastorno del sueño (insomnio, apnea del sueño, síndrome de piernas inquietas, etc.)?",
    options: ["Sí", "No", "No estoy seguro"],
  },
  {
    question:
      "¿Cuáles de los siguientes problemas has experimentado en relación con tu sueño? (Selecciona todas las que apliquen)",
    options: [
      "Dificultad para conciliar el sueño",
      "Despertares frecuentes durante la noche",
      "Sensación de fatiga al despertar",
      "Ronquidos fuertes o apnea del sueño",
      "Sueño no reparador",
      "Pesadillas o terrores nocturnos",
      "Otros (especificar)",
    ],
  },
  {
    question:
      "¿Cuánto impactan los problemas de sueño en tu vida diaria (trabajo, relaciones, salud emocional)?",
    options: ["Mucho", "Moderadamente", "Un poco", "Nada"],
  },
  {
    question: "¿Has buscado ayuda profesional para problemas de sueño?",
    options: ["Sí", "No", "No, pero lo consideraría"],
  },
  {
    question:
      "En tu opinión, ¿crees que los trastornos del sueño son un problema importante en la sociedad actual?",
    options: [
      "Sí, definitivamente",
      "Sí, en cierta medida",
      "No, no creo que sea un problema mayor",
      "No estoy seguro",
    ],
  },
  {
    question:
      "¿Crees que hay suficiente información y apoyo disponible sobre trastornos del sueño para las personas que los sufren?",
    options: [
      "Sí, hay suficiente información",
      "Hay información, pero no suficiente",
      "No, no hay suficiente información",
      "No lo sé",
    ],
  },
  {
    question:
      "¿Cuáles crees que son las principales barreras para que las personas busquen tratamiento para los trastornos del sueño? (Selecciona todas las que apliquen)",
    options: [
      "Falta de conciencia sobre el problema",
      "Miedo a los tratamientos médicos",
      "Costos asociados",
      "Estigma social",
      "Falta de tiempo",
      "Otros (especificar)",
    ],
  },
  {
    question:
      "¿Consideras que el estrés, la ansiedad o el estilo de vida tienen un impacto significativo en los problemas de sueño?",
    options: ["Sí", "No", "No estoy seguro"],
  },
  {
    question:
      "¿Crees que los trastornos del sueño están relacionados con otras condiciones de salud, como la depresión, ansiedad, enfermedades cardíacas, etc.?",
    options: [
      "Sí, están muy relacionados",
      "Sí, pero no en todos los casos",
      "No creo que estén relacionados",
      "No lo sé",
    ],
  },
  {
    question:
      "¿Te parecería útil contar con más recursos educativos, terapias o tecnología accesible para gestionar los trastornos del sueño?",
    options: [
      "Sí, definitivamente",
      "Sí, en cierta medida",
      "No, no lo creo",
      "No estoy seguro",
    ],
  },
  {
    question:
      "¿Tienes alguna sugerencia sobre cómo se podría mejorar la conciencia o el tratamiento de los trastornos del sueño en la sociedad?",
    options: [],
  },
];

export default function App() {
  return (
    <div className="app">
      <Header />
      <Form />
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <img src="" alt="delta icon" />
      <h1>Transtornos del sueño</h1>
      <h2>Bienvenido</h2>
      <Login />
    </header>
  );
}

function Login() {
  return (
    <>
      <p>Ingresar</p>
      <input type="text" value="" placeholder="Matricula" />
    </>
  );
}

function Form() {
  const questions = initialQuestions;

  return (
    <form>
      <ul>
        {questions.map((question) => (
          <Question question={question} />
        ))}
      </ul>
    </form>
  );
}

function Question(question) {
  return;
}
