}

function Login({ actualUser, onLogin }) {
  const users = initialUsers;

  return (
    <div className="login">
      <p>Ingresar</p>
      <input
        type="text"
        placeholder="Matricula institucional"
        value={actualUser}
        onChange={onLogin}
      />
    </div>
  );
}

function Form() {
  const questions = initialQuestions;
  const [actualQuestion, setActualQuestion] = useState(1);

  function handleNextQuestion() {
    setActualQuestion((cur) => (cur < questions.length ? cur + 1 : cur));
  }

  function handlePreviousQuestion() {
    setActualQuestion((cur) => (cur > 1 ? cur - 1 : cur));
  }

  function handleSubmit() {
    console.log("hola");
  }

  return (
    <>
      <form className="entire-form" onSubmit={handleSubmit}>
        <ul>
          {questions.map((question) => (
            <Question
              question={question}
              key={question.question}
              actualQuestion={actualQuestion}
            />
          ))}
        </ul>
      </form>
      <div className="buttons">
        <Button onClick={handlePreviousQuestion} type="button">
          Anterior
        </Button>
        <Button onClick={handleNextQuestion} type="button">
          Siguiente
        </Button>
      </div>
    </>
  );
}

function Button({ children, onClick, type }) {
  return (
    <button type={type} onClick={onClick}>
      {children}
    </button>
  );
}

function Question({ question, actualQuestion }) {
  return (
    <div className="question">
      {actualQuestion === question.num ? (
        <li>
          <p>{question.question}</p>
          <>
            {question.options.length > 0 ? (
              question.options.map((option, i) => (
                <Option num={question.num} option={option} key={option} i={i} />
              ))
            ) : (
              <textarea name={question.num} rows={4} cols={50} />
            )}
          </>
        </li>
      ) : null}
    </div>
  );
}

function Option({ num, option, i }) {
  return (
    <>
      <input type="radio" name={num} value={i} />
      <label>{option}</label>
      <br />
    </>
  );
}

